import React, { useState } from "react";
import ListaPedidos from "./ListaPedido";  // Display the list of orders
import CreateOrder from "./CreateOrder";    // Allow user to create new order

const App = () => {
  const [pedidos, setPedidos] = useState([]);  // State to hold orders

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-200 via-blue-400 to-indigo-500">
      <div className="container mx-auto p-8">
        {/* Heading */}
        <h1 className="text-4xl font-extrabold text-center text-white mb-8">Sistema de Gest√£o de Pedidos</h1>

        {/* Create Order Form Section */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Crie um novo Pedido!</h2>
          <CreateOrder setPedidos={setPedidos} />  {/* Passing the setPedidos function */}
        </div>

        {/* Orders List Section */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Lista de Pedidos</h2>
          <ListaPedidos pedidos={pedidos} setPedidos={setPedidos} />  {/* Passing pedidos and setPedidos */}
        </div>
      </div>
    </div>
  );
};

export default App;
