import React, { useState, useEffect } from "react";
import * as signalR from "@microsoft/signalr";
import { buscarPedidos, atualizarPedido, deletarPedido } from "./servicos/api"; // Ensure correct imports

const ListaPedidos = () => {
  const [pedidos, setPedidos] = useState([]);
  const [expandedOrderId, setExpandedOrderId] = useState(null);
  const [editingOrderId, setEditingOrderId] = useState(null);
  const [editData, setEditData] = useState({ cliente: "", produto: "", valor: "" });

  useEffect(() => {
    const fetchPedidos = async () => {
      const fetchedPedidos = await buscarPedidos();
      setPedidos(fetchedPedidos);
    };
    fetchPedidos();

    // Setup SignalR connection
    const connection = new signalR.HubConnectionBuilder()
      .withUrl("http://localhost:5000/orderHub")
      .build();

    connection.start()
      .then(() => console.log("Connected to SignalR"))
      .catch(err => console.log("Error while connecting: " + err));

    // Listen for real-time updates from the server
    connection.on("ReceiveOrderUpdate", (updatedOrder) => {
      setPedidos((prevPedidos) => 
        prevPedidos.map((pedido) => 
          pedido.id === updatedOrder.id ? { ...pedido, ...updatedOrder } : pedido
        )
      );
    });

    // Clean up connection on unmount
    return () => {
      connection.stop();
    };
  }, []);

  const handleToggleExpand = (orderId) => {
    setExpandedOrderId(expandedOrderId === orderId ? null : orderId);
  };

  const handleEditClick = (pedido) => {
    setEditingOrderId(pedido.id);
    setEditData({ cliente: pedido.cliente, produto: pedido.produto, valor: pedido.valor });
  };

  const handleCancelEdit = () => {
    setEditingOrderId(null);
  };

  const handleInputChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  const handleSaveEdit = async (orderId) => {
    await atualizarPedido(orderId, editData);  // API call to update order
    setPedidos(pedidos.map((pedido) => (pedido.id === orderId ? { ...pedido, ...editData } : pedido)));
    setEditingOrderId(null);
  };

  const handleDelete = async (orderId) => {
    await deletarPedido(orderId);  // API call to delete order
    setPedidos(pedidos.filter((pedido) => pedido.id !== orderId));
  };

  return (
    <div>
      <h2 className="text-xl font-semibold">Pedidos</h2>
      <div className="mt-4 space-y-4">
        {pedidos.map((pedido) => (
          <div
            key={pedido.id}
            className="border border-gray-300 p-4 rounded-lg shadow-md cursor-pointer"
            onClick={() => handleToggleExpand(pedido.id)}
          >
            <div className="flex justify-between">
              <span className="font-semibold">ID: {pedido.id}</span>
              <span>{pedido.cliente}</span>
            </div>
            <div className="flex justify-between">
              <span>{pedido.produto}</span>
              <span>{pedido.valor}</span>
            </div>

            {expandedOrderId === pedido.id && (
              <div className="mt-2 text-sm text-gray-600">
                <p>Status: {pedido.status}</p>
                <p>Data de Criação: {pedido.dataCriacao}</p>

                {editingOrderId === pedido.id ? (
                  <div className="mt-2">
                    <input
                      type="text"
                      name="cliente"
                      value={editData.cliente}
                      onChange={handleInputChange}
                      className="border p-1 rounded w-full mb-1"
                      placeholder="Cliente"
                    />
                    <input
                      type="text"
                      name="produto"
                      value={editData.produto}
                      onChange={handleInputChange}
                      className="border p-1 rounded w-full mb-1"
                      placeholder="Produto"
                    />
                    <input
                      type="text"
                      name="valor"
                      value={editData.valor}
                      onChange={handleInputChange}
                      className="border p-1 rounded w-full mb-1"
                      placeholder="Valor"
                    />
                    <div className="flex gap-2 mt-2">
                      <button onClick={() => handleSaveEdit(pedido.id)} className="bg-green-500 text-white px-3 py-1 rounded">
                        Save
                      </button>
                      <button onClick={handleCancelEdit} className="bg-gray-400 text-white px-3 py-1 rounded">
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <div className="flex gap-2 mt-2">
                    <button onClick={() => handleEditClick(pedido)} className="bg-blue-500 text-white px-3 py-1 rounded">
                      Edit
                    </button>
                    <button onClick={() => handleDelete(pedido.id)} className="bg-red-500 text-white px-3 py-1 rounded">
                      Delete
                    </button>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ListaPedidos;
