import React, { useEffect, useState } from "react";
import { buscarPedidos } from "./servicos/api";

const ListaPedidos = () => {
  const [pedidos, setPedidos] = useState([]);

  useEffect(() => {
    const fetchPedidos = async () => {
      const result = await buscarPedidos();
      setPedidos(result);
    };

    fetchPedidos();
  }, []);

  return (
    <div className="overflow-x-auto bg-white shadow-lg rounded-lg">
      <table className="min-w-full table-auto">
        <thead>
          <tr className="bg-blue-500 text-white">
            <th className="py-2 px-4 text-left">ID</th>
            <th className="py-2 px-4 text-left">Cliente</th>
            <th className="py-2 px-4 text-left">Produto</th>
            <th className="py-2 px-4 text-left">Valor</th>
            <th className="py-2 px-4 text-left">Status</th>
            <th className="py-2 px-4 text-left">Data Criacao</th>
          </tr>
        </thead>
        <tbody>
          {pedidos.map((pedido) => (
            <tr
              key={pedido.ID}
              className="border-b hover:bg-gray-100 cursor-pointer"
              onClick={() => alert(`Order details: ${JSON.stringify(pedido)}`)} // Show details on click
            >
              <td className="py-2 px-4">{pedido.ID}</td>
              <td className="py-2 px-4">{pedido.Cliente}</td>
              <td className="py-2 px-4">{pedido.Produto}</td>
              <td className="py-2 px-4">{pedido.Valor}</td>
              <td className="py-2 px-4">{pedido.Status}</td>
              <td className="py-2 px-4">{pedido.DataCriacao}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListaPedidos;
