import React, { useState } from "react";
import { cadastrarPedido, buscarPedidos } from "./servicos/api"; // Ensure correct imports

const CreateOrder = ({ setPedidos }) => {  // Get setPedidos as a prop
  const [order, setOrder] = useState({
    Cliente: "",
    Produto: "",
    Valor: "",
    Status: "Pendente",
    DataCriacao: new Date().toISOString(),
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setOrder({
      ...order,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Convert valor to a number (ensures correct data type)
    const formattedOrder = {
      ...order,
      Valor: parseFloat(order.Valor), // Converts "Valor" to a decimal number
    };

    await cadastrarPedido(formattedOrder);
    alert("Pedido criado com sucesso!");

    // Fetch updated orders and update the state
    const fetchedPedidos = await buscarPedidos();
    setPedidos(fetchedPedidos);  // Update the parent state with the new list of orders

    // Reset form after submission
    setOrder({
      Cliente: "",
      Produto: "",
      Valor: "",
      Status: "Pendente",
      DataCriacao: new Date().toISOString(),
    });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Cliente</label>
          <input
            type="text"
            name="Cliente"
            value={order.Cliente}
            onChange={handleChange}
            className="w-full px-4 py-2 border rounded-md"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">Produto</label>
          <input
            type="text"
            name="Produto"
            value={order.Produto}
            onChange={handleChange}
            className="w-full px-4 py-2 border rounded-md"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">Valor</label>
          <input
            type="number"
            name="Valor"
            value={order.Valor}
            onChange={handleChange}
            step="0.01" // Allows decimal values
            className="w-full px-4 py-2 border rounded-md"
            required
          />
        </div>

        <div className="mb-4">
          <label className="block text-gray-700">Status</label>
          <select
            name="Status"
            value={order.Status}
            onChange={handleChange}
            className="w-full px-4 py-2 border rounded-md"
          >
            <option value="Pendente">Pendente</option>
            <option value="Processando">Processando</option>
            <option value="Finalizado">Finalizado</option>
          </select>
        </div>

        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600"
        >
          Criar Pedido
        </button>
      </form>
    </div>
  );
};

export default CreateOrder;
